import React from 'react';
import { Button, Row, Col, Container, Table } from "react-bootstrap";
import io from 'socket.io-client';
import Modal from 'react-modal';

import API from '../../utils/API';

const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)'
    }
  };

export class Dashboard extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            all: [],
            modalIsOpen: false
        }
        this.disconnect.bind(this);
        this.socket = io('http://localhost:8000');

        this.socket.on('all', (data) => {
            getData(data);
        });

        const getData = data => {
            this.setState({ all: data});
        };

        this.openModal = this.openModal.bind(this);
        this.afterOpenModal = this.afterOpenModal.bind(this);
        this.closeModal = this.closeModal.bind(this);
    }

    openModal() {
        this.setState({modalIsOpen: true});
      }

      afterOpenModal() {
        // references are now sync'd and can be accessed.
        this.subtitle.style.color = '#f00';
      }

      closeModal() {
        this.setState({modalIsOpen: false});
      }

    disconnect = event => {
        API.logout();
        window.location = "/";
    }
    delete = event => {
        event.preventDefault();
        var idint = parseInt(event.target.name, 10);
        this.socket.emit('delete', idint);
    }

    render() {
        return(
            <div className="Dashboard">
                <Container>
                    <Row>
                        <Col>
                            <h1>Dashboard</h1>
                        </Col>
                        <Col>
                            <Button
                            onClick={this.disconnect}
                            block
                            type="submit"
                            >
                            Se d√©connecter
                            </Button>
                        </Col>
                    </Row>
                    <Row>
                        <Col>
                            <div>
                                <br></br>
                                <Table responsive>
                                    <thead>
                                        <tr>
                                            <th className="text-center">Name</th>
                                            <th className="text-center">Type</th>
                                            <th className="text-center">Price</th>
                                            <th className="text-center">Rating</th>
                                            <th className="text-center">Warranty years</th>
                                            <th className="text-center">Edit</th>
                                            <th className="text-center">Delete</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {this.state.all.map(get =>
                                            <tr key={get._id}>
                                                <td>{get.name}</td>
                                                <td>{get.type}</td>
                                                <td>{get.price}</td>
                                                <td>{get.rating}</td>
                                                <td>{get.warranty_years}</td>
                                                <td>
                                                <Button
                                                    onClick={this.openModal}
                                                    block
                                                    products={this}
                                                    type="submit"
                                                    name={get}
                                                    >
                                                Edit
                                                </Button>
                                                </td>
                                                <td><Button 
                                                    variant="danger"
                                                    onClick={this.delete}
                                                    block
                                                    type="submit"
                                                    name={get._id}
                                                    >X
                                                    </Button></td>
                                            </tr>
                                        )}
                                    </tbody>
                                </Table>
                            </div>
                        </Col>
                        <Col>
                            <br></br>
                            <Button variant="success" style={{width: '200px', float: 'right'}}>New</Button>
                            <br></br>
                        </Col>
                    </Row>
                </Container>
                <Modal
                    isOpen={this.state.modalIsOpen}
                    onAfterOpen={this.afterOpenModal}
                    onRequestClose={this.closeModal}
                    style={customStyles}
                    contentLabel="Example Modal"
                    >

                    <h2 ref={subtitle => this.subtitle = subtitle}>Hello</h2>
                    <button onClick={this.closeModal}>close</button>

                </Modal>
            </div>
        )
    }
}
