import React from 'react';
import { Button, Component, Row, Col, Container } from "react-bootstrap";
import io from 'socket.io-client';

import API from '../../utils/API';

export class Dashboard extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            all: []
        }
        this.disconnect.bind(this);
        this.socket = io('http://localhost:8000');

        this.socket.on('all', (data) => {
            getData(data);
        });

        const getData = data => {
            this.setState({ all: data});
        };
    }

    disconnect = event => {
        API.logout();
        window.location = "/";
    }
    render() {
        return(
            <div className="Dashboard">
                <Container>
                    <Row>
                        <Col>
                            <h1>Dashboard</h1>
                        </Col>
                        <Col>
                            <Button
                            onClick={this.disconnect}
                            block
                            type="submit"
                            >
                            Se d√©connecter
                            </Button>
                        </Col>
                    </Row>
                    <Row>
                        <Col>
                            <div>
                                {this.state.all.map(get =>
                                    <div>
                                        <h1>{get.name}</h1>
                                        <hr></hr>
                                        <h2>{get.type}</h2>
                                        <h3>{get.price}</h3>
                                        <h4>{get.rating}</h4>
                                        <h5>{get.warranty_years}</h5>
                                        <br></br>
                                    </div>
                                )}
                            </div>
                        </Col>
                    </Row>    
                </Container>              
            </div>
        )
    }
}
