import React from 'react';
import { Button, Row, Col, Container, Table } from "react-bootstrap";
import io from 'socket.io-client';
import Modal from 'react-bootstrap-modal';

import API from '../../utils/API';

export class Dashboard extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            all: [],
            open: false
        }
        this.disconnect.bind(this);
        this.socket = io('http://localhost:8000');

        this.socket.on('all', (data) => {
            getData(data);
        });

        const getData = data => {
            this.setState({ all: data});
        };
    }

    disconnect = event => {
        API.logout();
        window.location = "/";
    }
    delete = event => {
        event.preventDefault();
        var idint = parseInt(event.target.name, 10);
        this.socket.emit('delete', idint);
    }

    render() {
        let closeModal = () => this.setState({ open: false })

        let saveAndClose = () => {
          this.setState({ open: false })
        }
        return(
            <div className="Dashboard">
                <button type='button'>Launch modal</button>
                        <Modal
          show={this.state.open}
          onHide={closeModal}
          aria-labelledby="ModalHeader"
        >
          <Modal.Header closeButton>
            <Modal.Title id='ModalHeader'>A Title Goes here</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>Some Content here</p>
          </Modal.Body>
          <Modal.Footer>
            <Modal.Dismiss className='btn btn-default'>Cancel</Modal.Dismiss>
            <button className='btn btn-primary' onClick={saveAndClose}>
              Save
            </button>
          </Modal.Footer>
        </Modal>
                <Container>
                    <Row>
                        <Col>
                            <h1>Dashboard</h1>
                        </Col>
                        <Col>
                            <Button
                            onClick={this.disconnect}
                            block
                            type="submit"
                            >
                            Se déconnecter
                            </Button>
                        </Col>
                    </Row>
                    <Row>
                        <Col>
                            <div>
                                <br></br>
                                <Table responsive>
                                    <thead>
                                        <tr>
                                            <th className="text-center">Name</th>
                                            <th className="text-center">Type</th>
                                            <th className="text-center">Price</th>
                                            <th className="text-center">Rating</th>
                                            <th className="text-center">Warranty years</th>
                                            <th className="text-center">Edit</th>
                                            <th className="text-center">Delete</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {this.state.all.map(get =>
                                            <tr key={get._id}>
                                                <td>{get.name}</td>
                                                <td>{get.type}</td>
                                                <td>{get.price}</td>
                                                <td>{get.rating}</td>
                                                <td>{get.warranty_years}</td>
                                                <td>
                                                <Button
                                                    onClick={this.edit}
                                                    block
                                                    type="submit"
                                                    name={get.id}
                                                    >
                                                Edit
                                                </Button>
                                                </td>
                                                <td><Button 
                                                    variant="danger"
                                                    onClick={this.delete}
                                                    block
                                                    type="submit"
                                                    name={get._id}
                                                    >X
                                                    </Button></td>
                                            </tr>
                                        )}
                                    </tbody>
                                </Table>
                            </div>
                        </Col>
                        <Col>
                            <br></br>
                            <Button variant="success" style={{width: '200px', float: 'right'}}>New</Button>
                            <br></br>
                        </Col>
                    </Row>
                </Container>
            </div>
        )
    }
}
